import React, { Component } from "react";
import axios from "axios";
import style from "./movie.module.css";
import Overview from "./Overview";
import Videos from "./Videos";
import Picture from "./Picture";
import Cast from "./Cast";
import Details from "./Details";
import List from "../../globalComponents/List";
import PictureModal from "../../globalComponents/PictureModal";

const Title = props => {
  const labelStyle = {
    color: "white",
    fontWeight: "bold",
    fontSize: "1.8em",
    marginLeft: "4px"
  };

  return (
    <div
      style={{
        position: "relative",
        display: "flex",
        alignItems: "center",
        gridColumnStart: "2"
      }}
    >
      <div
        style={{
          width: "4px",
          height: "28px",
          backgroundColor: "#c7493a"
        }}
      ></div>
      <label className="component-label" style={labelStyle}>
        {props.titleName}
      </label>
      {props.more === true && (
        <span
          style={{
            position: "absolute",
            right: "0px",
            bottom: "0px",
            fontSize: "1.2em",
            color: "#c7493a"
          }}
        >
          View All
        </span>
      )}
    </div>
  );
};

const ModalBackground = props => {
  return (
    <div
      style={{
        position: "fixed",
        width: "100%",
        height: "110vh",
        top: "0px",
        background: "rgba(0, 0, 0, 0.6)",
        zIndex: 20
      }}
      onClick={props.closeModal}
    ></div>
  );
};

export class Movie extends Component {
  movieID = () => {
    const { movieID } = this.props.match.params;
    return movieID;
  };

  state = {
    apiKey: "325d84ad33eb95a5c0fc5427ba3f569a",
    movieID: this.movieID(),
    details: {},
    posters: [],
    backdrops: []
  };

  setImages = (posters, backdrops) => {
    this.setState(() => {
      return {
        posters: [...posters],
        backdrops: [...backdrops]
      };
    });
  };

  closeModal = () => {
    this.pictureModal.current.style.opacity = "0";
  };

  async componentDidMount() {
    this.pictureModal = React.createRef();
    const { apiKey, movieID } = this.state;

    await axios
      .get(
        `https://api.themoviedb.org/3/movie/${movieID}?api_key=${apiKey}&language=en-US`
      )
      .then(res => {
        this.setState(() => {
          return {
            details: res.data
          };
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div className={style.movie}>
        <ModalBackground closeModal={this.closeModal} />
        <PictureModal
          ref={this.pictureModal}
          apiKey={this.state.apiKey}
          movieID={this.state.movieID}
          posters={this.state.posters}
          backdrops={this.state.backdrops}
        />
        <Overview details={this.state.details} />
        <Title titleName="Videos" />
        <Videos apiKey={this.state.apiKey} movieID={this.state.movieID} />
        <Title titleName="Pictures" more={true} />
        <Picture
          apiKey={this.state.apiKey}
          movieID={this.state.movieID}
          setImages={this.setImages}
        />
        <Title titleName="Cast" more={true} />
        <Cast apiKey={this.state.apiKey} movieID={this.state.movieID} />
        <Title titleName="Similar movies" />
        <List
          api={`https://api.themoviedb.org/3/movie/${this.movieID()}/similar?api_key=${
            this.state.apiKey
          }&language=en-US&page=1`}
          isMovie={true}
        />

        <Title titleName="Details" />
        <Details details={this.state.details} />
      </div>
    );
  }
}

export default Movie;
