import React, { Component } from "react";
import style from "./people.module.css";
import axios from "axios";

const Person = props => {
  const { picturePath, name } = props;

  return (
    <div className={style.person}>
      <div className="picture-container">
        <img
          src={`https://image.tmdb.org/t/p/w185${picturePath}`}
          alt="person photo"
        />
      </div>
      <div className={style["info-container"]}>
        <span>{name}</span>
      </div>
    </div>
  );
};

export class People extends Component {
  state = {
    apiKey: "325d84ad33eb95a5c0fc5427ba3f569a",
    people: [],
    page: ""
  };

  async componentDidMount() {
    const { apiKey } = this.state;
    const { page } = this.props.match.params;

    await axios
      .get(
        `https://api.themoviedb.org/3/person/popular?api_key=${apiKey}&language=en-US&page=${page}`
      )
      .then(res => {
        this.setState(() => {
          return {
            people: [...res.data.results]
          };
        });
      })
      .catch(err => console.log(err));

    console.log(this.state.people);
  }

  render() {
    const { people } = this.state;

    return (
      <div className={style.people}>
        <div className={style["people-container"]}>
          {people.map(p => (
            <Person picturePath={p.profile_path} name={p.name} />
          ))}
        </div>
      </div>
    );
  }
}

export default People;
