import React from "react";
import style from "./global.module.css";
import Title from "./Title";
import CloseIcon from "@material-ui/icons/Close";

const PosterRow = props => {
  return (
    <div className={style["poster-row"]}>
      {props.posters.map(p => (
        <img
          src={`https://image.tmdb.org/t/p/w185/${p.file_path}`}
          alt="poster image"
          onClick={() => {
            props.openNewWindow(p.file_path);
          }}
        />
      ))}
    </div>
  );
};

const BackdropRow = props => {
  return (
    <div className={style["backdrop-row"]}>
      {props.backdrops.map(b => (
        <img src={`https://image.tmdb.org/t/p/w300/${b.file_path}`} />
      ))}
    </div>
  );
};

class PictureModal extends React.Component {
  openNewWindow = imgPath => {
    window.open(window.origin + "/image" + imgPath, "_blank");
  };

  render() {
    const { posters, backdrops } = this.props;

    return (
      <div className={style["pictures-modal"]}>
        <CloseIcon className={style.closeIcon} />
        <Title titleName="Posters" className={style.title} />
        {posters && (
          <PosterRow posters={posters} openNewWindow={this.openNewWindow} />
        )}
        <Title titleName="Backdrops" />
        <BackdropRow backdrops={backdrops} />
      </div>
    );
  }
}

export default PictureModal;
