import React, { Component } from "react";
import axios from "axios";
import style from "./movie.module.css";

const PictureContainer = props => {
  return (
    <div className={style["picture-container"]}>
      <div className={style.picture}></div>
    </div>
  );
};

class Picture extends Component {
  state = {
    backdrops: [],
    posters: []
  };

  async componentDidMount() {
    await axios
      .get(
        `https://api.themoviedb.org/3/movie/${this.props.movieID}/images?api_key=${this.props.apiKey}`
      )
      .then(res => {
        this.setState(() => {
          return {
            backdrops: [...res.data.backdrops],
            posters: [...res.data.posters]
          };
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div className={style.picture}>
        <PictureContainer pictures={this.state.backdrops} />
      </div>
    );
  }
}

export default Picture;
