import React from "react";
import PropTypes from "prop-types";
import axios from "axios";

const Title = props => {
  const labelStyle = {
    color: "white",
    fontWeight: "bold",
    fontSize: "1.8em",
    marginLeft: "4px"
  };

  return (
    <div style={{ display: "flex", alignItems: "center" }}>
      <div
        style={{
          width: "4px",
          height: "28px",
          backgroundColor: "#c7493a"
        }}
      ></div>
      <label className="component-label" style={labelStyle}>
        {props.titleName}
      </label>
    </div>
  );
};

const Featured = props => {
  return (
    <div className="featured">
      <Title titleName="Featured" />
    </div>
  );
};

class Home extends React.Component {
  state = {
    trending: [{}]
  };

  componentDidMount() {
    const apiKey = "325d84ad33eb95a5c0fc5427ba3f569a";
    //get trending / featured
    axios
      .get(`https://api.themoviedb.org/3/trending/movie/day?api_key=${apiKey}`)
      .then(response => {
        console.log(response.data.results);
        this.setState(() => {
          return {
            trending: [...response.data.results]
          };
        });
        console.log(this.state.results);
      })
      .catch(error => {
        console.log("error: " + error);
      });
  }

  render() {
    return (
      <div className="home">
        <Featured />
      </div>
    );
  }
}

export default Home;
