import React from "react";
import style from "./global.module.css";
import Title from "./Title";
import CloseIcon from "@material-ui/icons/Close";

const Cast = props => {
  return (
    <div className={style.cast}>
      {props.image !== null ? (
        <img src={`https://image.tmdb.org/t/p/w185/${props.image}`} alt="" />
      ) : (
        <img
          className={style["empty-image"]}
          src={require("../../../src/assets/person.png")}
          alt="empty person image"
        />
      )}

      <div className={style["cast-description"]}>
        <span className={style["cast-name"]}>{props.castName}</span>
        <span>{props.character}</span>
      </div>
    </div>
  );
};

class CastModal extends React.Component {
  closeModal = () => {
    this.modal.current.style.visibility = "hidden";
    this.modal.current.style.opacity = "0";
    this.modal.current.style.transition = "all 200ms ease-in";
  };

  componentDidMount() {
    this.modal = React.createRef();
  }

  render() {
    const { cast, crew } = this.props;

    return (
      <div ref={this.modal} className={style["cast-modal"]}>
        <CloseIcon className={style.closeIcon} onClick={this.closeModal} />
        <Title titleName="Cast" />
        <div className={style["cast-container"]}>
          {cast.map(c => (
            <Cast
              key={c.cast_id}
              image={c.profile_path}
              castName={c.name}
              character={c.character}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default CastModal;
