import React from "react";
import PropTypes from "prop-types";
import axios from "axios";

const Title = props => {
  const labelStyle = {
    color: "white",
    fontWeight: "bold",
    fontSize: "1.8em",
    marginLeft: "4px"
  };

  return (
    <div style={{ display: "flex", alignItems: "center" }}>
      <div
        style={{
          width: "4px",
          height: "28px",
          backgroundColor: "#c7493a"
        }}
      ></div>
      <label className="component-label" style={labelStyle}>
        {props.titleName}
      </label>
    </div>
  );
};
// Todo: redo featured in a seperate component
const Featured = props => {
  return (
    <div className="featured">
      <Title titleName="Featured" />

      <div className="movies">
        <div className="carousel">
          {props.trending.map((t, i) => (
            <div style={{ display: "flex", width: "60vw" }}>
              <div className="poster">
                <img
                  src={`https://image.tmdb.org/t/p/original${t.poster_path}`}
                  alt=""
                />
              </div>
              <div className="backdrop">
                <img
                  src={`https://image.tmdb.org/t/p/original${t.backdrop_path}`}
                  alt=""
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

class Home extends React.Component {
  state = {
    trending: []
  };

  componentDidMount() {
    const apiKey = "325d84ad33eb95a5c0fc5427ba3f569a";
    //get trending / featured
    axios
      .get(`https://api.themoviedb.org/3/trending/movie/day?api_key=${apiKey}`)
      .then(response => {
        this.setState(() => {
          return {
            trending: [...response.data.results]
          };
        });
        console.log(this.state.trending);
      })
      .catch(error => {
        console.log("error: " + error);
      });
  }

  render() {
    return (
      <div className="home">
        <Featured trending={this.state.trending} />
      </div>
    );
  }
}

export default Home;
