import React, { Component } from "react";
import style from "./movie_tv.module.css";

const Season = props => {
  return (
    <div className={style.season}>
      {props.poster_path && (
        <>
          <img
            src={`https://image.tmdb.org/t/p/w185/${props.poster}`}
            alt="season poster"
          />
          <div className={style["season-details"]}></div>
        </>
      )}
    </div>
  );
};

export class Seasons extends Component {
  render() {
    const { seriesName, seasons } = this.props;
    return (
      <div className={style["seasons-container"]}>
        {seasons && seasons.map(s => <Season poster={s.poster_path} />)}
      </div>
    );
  }
}

export default Seasons;
