import React from "react";
import style from "./person.module.css";
import axios from "axios";

const Biography = props => {
  const { details } = props;
  return (
    <div className={style.biography}>
      <img
        src={`https://image.tmdb.org/t/p/w185/${details.profile_path}`}
        alt="person image"
      />
    </div>
  );
};

class Person extends React.Component {
  personID = () => {
    const { personID } = this.props.match.params;
    return personID;
  };

  state = {
    apiKey: "325d84ad33eb95a5c0fc5427ba3f569a",
    personID: this.personID(),
    personDetails: {}
  };

  async componentDidMount() {
    const { apiKey, personID } = this.state;
    await axios
      .get(
        `https://api.themoviedb.org/3/person/${personID}?api_key=${apiKey}&language=en-US`
      )
      .then(res => {
        this.setState(() => {
          return {
            personDetails: res.data
          };
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div className={style.person}>
        <Biography details={this.state.personDetails} />
      </div>
    );
  }
}

export default Person;
