import React, { Component } from "react";
import style from "./movies_tvs.module.css";

const Select = props => {
  const { placeholder } = props;
  return (
    <select className={style["select"]}>
      <option value="" disabled selected>
        {placeholder}
      </option>
      {props.options.map(op => (
        <option>{op}</option>
      ))}
    </select>
  );
};

export class Filter extends Component {
  constructor(props) {
    super(props);

    this.state = {
      years: ["2020", "2019"],
      width: ""
    };

    this.inputContainer = React.createRef();
    this.selectContainer = React.createRef();
  }

  componentDidMount() {
    let date = new Date();
    console.log(date.getFullYear());
  }

  render() {
    return (
      <div className={style.filter}>
        <div ref={this.inputContainer} className={style["input-container"]}>
          <input
            onChange={e => this.props.onSearch(e)}
            type="text"
            placeholder="Keyword"
          />
        </div>
        <div ref={this.selectContainer} className={style["select-container"]}>
          <Select placeholder="Year" options={this.state.years} />
          <Select placeholder="Genre" />
          <Select placeholder="Sort by" />
        </div>
      </div>
    );
  }
}

export default Filter;
