import React, { Component } from "react";
import { Link } from "react-router-dom";
import Search from "@material-ui/icons/Search";
import MenuIcon from "@material-ui/icons/Menu";

class Navbar extends Component {
  state = {
    Nav: [
      {
        name: "Discover",
        link: "/discover"
      },
      {
        name: "Movies",
        link: "/movies"
      },
      {
        name: "TV shows",
        link: "/tvshows"
      },
      {
        name: "People",
        link: "/people"
      },
      {
        name: "Genre",
        link: "/genre"
      }
    ]
  };

  onMenuClick = () => {
    console.log("click");
    let navbar = document.querySelector(".navbar");
    let linkContainer = document.querySelector(".link-container");

    linkContainer.classList.toggle("link-container-active");
  };

  render() {
    return (
      <div className="navbar">
        <span className="logo">Mophoria</span>
        <div className="link-container">
          {this.state.Nav.map(nav => (
            <Link className="link" to={nav.link}>
              {nav.name}
            </Link>
          ))}
        </div>

        <div className="search-button">
          <Search className="search" fontSize="medium" />
        </div>

        <div className="menu-button" onClick={this.onMenuClick}>
          <MenuIcon />
        </div>
      </div>
    );
  }
}

export default Navbar;
