import React, { Component } from "react";
import style from "./movie_tv.module.css";

const Season = props => {
  const { poster, seasonName } = props;
  return (
    <div className={style.season}>
      {props.poster_path !== null && (
        <>
          <img
            src={`https://image.tmdb.org/t/p/w185/${poster}`}
            alt="season poster"
          />
          <div className={style["season-details"]}>
            <span className={style["season-name"]}>{seasonName}</span>
          </div>
        </>
      )}
    </div>
  );
};

export class Seasons extends Component {
  render() {
    const { seriesName, seasons } = this.props;
    return (
      <div className={style["seasons-container"]}>
        {seasons &&
          seasons.map(s => (
            <Season poster={s.poster_path} seasonName={s.name} />
          ))}
      </div>
    );
  }
}

export default Seasons;
