import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import SearchIcon from "@material-ui/icons/Search";
import MenuIcon from "@material-ui/icons/Menu";

const getYear = str => {
  let string = String(str);
  return string.substring(0, 4);
};

const itemDescription = item => {
  switch (item.media_type) {
    case "movie":
      return (
        <React.Fragment>
          <span>{item.title}</span>
          <span>{`${getYear(item.release_date)} ${item.media_type}`}</span>
        </React.Fragment>
      );
    case "tv":
      return (
        <React.Fragment>
          <span>{item.title}</span>
          <span>{`${getYear(item.first_air_date)} ${
            item.media_type
          } show`}</span>
        </React.Fragment>
      );
    case "person":
      return (
        <React.Fragment>
          <span>{item.name}</span>
          <span>{item.media_type}</span>
        </React.Fragment>
      );
  }
};

const Search = props => {
  return (
    <div className="search-container">
      <input
        type="text"
        onChange={props.onSearch}
        placeholder="Search mophoria.com"
      />
      <div className="results">
        {props.SearchedItems !== null &&
          props.SearchedItems.map(item => (
            <div className="item">
              {item.media_type === "movie" ? (
                <img
                  src={`https://image.tmdb.org/t/p/w185${item.poster_path}`}
                  alt="item poster"
                />
              ) : (
                <img
                  src={`https://image.tmdb.org/t/p/w185${item.profile_path}`}
                  alt="item poster"
                />
              )}

              <div style={{ display: "flex", flexDirection: "column" }}>
                {itemDescription(item)}
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

class Navbar extends Component {
  state = {
    Nav: [
      {
        name: "Discover",
        link: "/discover"
      },
      {
        name: "Movies",
        link: "/movies"
      },
      {
        name: "TV shows",
        link: "/tvshows"
      },
      {
        name: "People",
        link: "/people"
      },
      {
        name: "Genre",
        link: "/genre"
      }
    ],
    SearchedItems: null
  };

  onMenuClick = () => {
    console.log("click");
    let navbar = document.querySelector(".navbar");
    let linkContainer = document.querySelector(".link-container");

    navbar.classList.toggle("navbar-active");
    linkContainer.classList.toggle("link-container-active");
  };

  apiKey = "325d84ad33eb95a5c0fc5427ba3f569a";
  onSearch = e => {
    axios
      .get(
        `https://api.themoviedb.org/3/search/multi?api_key=${this.apiKey}&language=en-US&page=1&include_adult=false&query=${e.target.value}`
      )
      .then(res => {
        this.setState(() => {
          return {
            SearchedItems: [...res.data.results]
          };
        });
        console.log(res.data.results);
      })
      .catch(err => {
        console.log(err);
      });
  };

  render() {
    return (
      <div className="navbar" style={{ zIndex: 1 }}>
        <Search
          onSearch={this.onSearch}
          SearchedItems={this.state.SearchedItems}
        />
        <span className="logo">Mophoria</span>
        <div className="link-container">
          {this.state.Nav.map((nav, i) => (
            <Link key={i} className="link" to={nav.link}>
              {nav.name}
            </Link>
          ))}
        </div>

        <div className="search-button">
          <SearchIcon className="search" fontSize="default" />
        </div>

        <div className="menu-button" onClick={this.onMenuClick}>
          <MenuIcon />
        </div>
      </div>
    );
  }
}

export default Navbar;
