import React from "react";
import axios from "axios";
import PersonIcon from "@material-ui/icons/Person";
import NavigateBeforeIcon from "@material-ui/icons/NavigateBefore";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";

const PersonName = props => {
  return (
    <div className="person-name">
      <span>{props.personName}</span>
    </div>
  );
};

const PrevButton = props => {
  return (
    <div className="prev-btn">
      <NavigateBeforeIcon className="component-prev" />
    </div>
  );
};

const NextButton = props => {
  return (
    <div className="next-btn">
      <NavigateNextIcon className="component-next" />
    </div>
  );
};

class People extends React.Component {
  async componentDidMount() {
    await axios
      .get(
        `https://api.themoviedb.org/3/trending/person/day?api_key=${this.props.apiKey}`
      )
      .then(res => {
        this.setState(() => {
          return {
            people: [...res.data.results]
          };
        });
      })
      .catch(err => console.log(err));
  }

  state = {
    people: []
  };

  render() {
    return (
      <div className="people">
        <div className="people-carousel-slide">
          {this.state.people.map(p => (
            <React.Fragment>
              <div className="people-image">
                {p.profile_path !== null ? (
                  <img
                    src={`https://image.tmdb.org/t/p/w185/${p.profile_path}`}
                    alt="person image"
                  />
                ) : (
                  <img
                    className="empty-image"
                    src={require("../../../../src/assets/person.png")}
                    alt="empty person image"
                  />
                )}

                <PersonName personName={p.name} />
              </div>
            </React.Fragment>
          ))}
        </div>
        <NextButton />
      </div>
    );
  }
}

export default People;
