import React from "react";
import Featured from "./Featured";
import Theaters from "./Theaters";

const Title = props => {
  const labelStyle = {
    color: "white",
    fontWeight: "bold",
    fontSize: "1.8em",
    marginLeft: "4px"
  };

  return (
    <div
      style={{ display: "flex", alignItems: "center", gridColumnStart: "2" }}
    >
      <div
        style={{
          width: "4px",
          height: "28px",
          backgroundColor: "#c7493a"
        }}
      ></div>
      <label className="component-label" style={labelStyle}>
        {props.titleName}
      </label>
    </div>
  );
};

class Home extends React.Component {
  apiKey = "325d84ad33eb95a5c0fc5427ba3f569a";
  render() {
    return (
      <div className="home">
        <Title titleName="Featured" />
        <Featured apiKey={this.apiKey} />
        <Title titleName="In theaters" />
        <div>d</div>
        <Theaters apiKey={this.apiKey} />
      </div>
    );
  }
}

export default Home;
