import React from "react";
import axios from "axios";
import PlayArrowIcon from "@material-ui/icons/PlayArrow";
import NavigateBeforeIcon from "@material-ui/icons/NavigateBefore";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";
import "./home.css";

const Carousel = props => {
  return (
    <div className="carousel-slide">
      {props.content.map(c => (
        <React.Fragment key={c.id}>
          <img
            className="poster"
            src={`https://image.tmdb.org/t/p/original${c.poster_path}`}
            alt=""
          />

          <img
            className="backdrop"
            src={`https://image.tmdb.org/t/p/original${c.backdrop_path}`}
            alt="movie backdrop"
          />
        </React.Fragment>
      ))}
    </div>
  );
};

//overlay
const PosterOverlay = props => {
  return (
    <div className="poster-overlay">
      {props.movie.map((m, i) => (
        <React.Fragment key={m.id}>
          {i === props.currentIndex && <span>{`${m.vote_average} / 10`}</span>}
        </React.Fragment>
      ))}
    </div>
  );
};

const BackdropOverlay = props => {
  return (
    <div className="backdrop-overlay">
      <PlayArrowIcon
        className="playBtn"
        onClick={props.onPlay}
        fontSize="large"
      />
      {props.movie.map(
        (m, i) =>
          i === props.currentIndex && (
            <span
              key={m.id}
            >{`${m.title} ${this.state.currentMovieVideos[0].type}`}</span>
          )
      )}
    </div>
  );
};

//prev and next btn
const PrevBtn = props => {
  return (
    <div className="prev-btn" onClick={props.onPrevClick}>
      <NavigateBeforeIcon fontSize="large" />
    </div>
  );
};

const NextBtn = props => {
  return (
    <div className="next-btn" onClick={props.onNextClick}>
      <NavigateNextIcon fontSize="large" />
    </div>
  );
};

//modal
const ModalVideo = props => {
  return (
    <div className="modal">
      <iframe
        src="https://www.youtube.com/embed/bwzLiQZDw2I?autoplay=1"
        title="trailer"
      ></iframe>
    </div>
  );
};

class Featured extends React.Component {
  onPlay = () => {
    console.log("click");
    const modal = document.querySelector(".modal");
    modal.classList.toggle("modal-active");
  };

  slideTransformValue = 0;
  onPrevClick = () => {
    const carouselSlide = document.querySelector(".carousel-slide");
    if (this.slideTransformValue > 0) {
      this.setState(() => {
        return { currentMovieIndex: this.state.currentMovieIndex - 1 };
      });
      this.slideTransformValue -= 100;
      carouselSlide.style.transform = `translateX(-${this.slideTransformValue}%)`;
    }
  };

  onNextClick = () => {
    const carouselSlide = document.querySelector(".carousel-slide");

    if (this.slideTransformValue < 1900) {
      this.setState(() => {
        return { currentMovieIndex: this.state.currentMovieIndex + 1 };
      });
      this.slideTransformValue += 100;
      carouselSlide.style.transform = `translateX(-${this.slideTransformValue}%)`;
    }
  };

  onWindowClick = () =>
    window.addEventListener("click", e => {
      const modal = document.querySelector(".modal");
      if (e.target.tagName === "HTML") {
        modal.classList.remove("modal-active");
      }
    });

  // STATE
  state = {
    currentMovieIndex: 0,
    currentMovieVideos: [],
    movieIDs: [],
    trending: []
  };

  componentDidMount() {
    //get trending movies
    axios
      .get(
        `https://api.themoviedb.org/3/trending/movie/day?api_key=${this.props.apiKey}`
      )
      .then(res => {
        this.setState(() => {
          return {
            trending: [...res.data.results]
          };
        });
      })
      .catch(err => {
        console.log(err);
      });

    //close modal
    this.onWindowClick();
    //auto slide
    // setInterval(() => {
    //   this.onNextClick();
    //
    // }, 4000);
  }

  setVideoTrailers = movieID => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie/${movieID}/videos?api_key=${this.props.apiKey}`
      )
      .then(res => {
        //.data.results
        this.setState(() => {
          return {
            currentMovieVideos: [...res.data.results]
          };
        });
        console.log(this.state.currentMovieVideos);
      })
      .catch(err => console.log(err));
  };

  componentDidUpdate(prevProps, prevState) {
    //if axios is done fetching data !
    if (prevState.trending.length !== this.state.trending.length) {
      // get trending ids and set to state
      let tempArray = [];
      for (let i = 0; i < this.state.trending.length; i++)
        tempArray.push(this.state.trending[i].id);

      this.setState(() => {
        return {
          movieIDs: [...tempArray]
        };
      });
    }

    //if movieIDs !== null
    if (prevState.movieIDs.length !== this.state.movieIDs.length) {
      //get first movie trailer from id 0
      this.setVideoTrailers(this.state.movieIDs[0]);
    }
  }

  render() {
    return (
      <div className="featured">
        <Carousel
          content={this.state.trending}
          current={this.state.currentMovieIndex}
        />
        <PrevBtn onPrevClick={this.onPrevClick} />
        <NextBtn onNextClick={this.onNextClick} />
        <BackdropOverlay
          movie={this.state.trending}
          currentIndex={this.state.currentMovieIndex}
          onPlay={this.onPlay}
        />
        <PosterOverlay
          movie={this.state.trending}
          currentIndex={this.state.currentMovieIndex}
        />
        <ModalVideo />
      </div>
    );
  }
}

export default Featured;
