import React from "react";
import style from "./movies_tvs.module.css";
import axios from "axios";
import Title from "../../globalComponents/Title";
import Item from "./Item";
import Filter from "./Filter";

class Movies extends React.Component {
  state = {
    apiKey: "325d84ad33eb95a5c0fc5427ba3f569a",
    items: [],
    itemsCopy: [],
    initialItemsValue: [],
    selectedGenreID: 0,
    type: "movie"
  };

  async componentDidMount() {
    const { apiKey } = this.state;
    //initial items
    await axios
      .get(
        `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&language=en-US&page=1`
      )
      .then(res => {
        this.setState(() => {
          return {
            items: res.data.results,
            initialItemsValue: res.data.results
          };
        });
      })
      .catch(err => console.log(err));

    console.log(this.state.items);
  }

  onSearch = async e => {
    //change items on search
    if (e.target.value === "") {
      this.setState(() => {
        return {
          items: [...this.state.initialItemsValue]
        };
      });
    } else {
      await axios
        .get(
          `https://api.themoviedb.org/3/search/movie?api_key=${this.state.apiKey}&language=en-US&page=1&include_adult=false&query=${e.target.value}`
        )
        .then(res => {
          this.setState(() => {
            return {
              items: res.data.results,
              itemsCopy: res.data.results
            };
          });
        })
        .catch(err => console.log(err));

      //filter search result based on selected genre
      this.onChangeGenre(this.state.selectedGenreID);
    }
  };

  onChangeGenre = genre => {
    if (genre !== this.state.selectedGenreID) {
      this.setState(() => {
        return {
          selectedGenreID: genre
        };
      });
    }

    const { items } = this.state;
    let newItems = [];

    if (String(genre) !== "All") {
      for (let i = 0; i < items.length; i++) {
        for (
          let genre_id = 0;
          genre_id < items[i].genre_ids.length;
          genre_id++
        ) {
          if (items[i].genre_ids[genre_id] === parseInt(genre)) {
            newItems.push(items[i]);
          }
        }
      }
      this.setState(() => {
        return {
          items: [...newItems]
        };
      });
    } else {
      this.setState(() => {
        return {
          items: [...this.state.itemsCopy]
        };
      });
    }
  };

  render() {
    const { items, type } = this.state;

    return (
      <div className={style.movies}>
        <Title titleName="Search movies" />
        <Filter
          apiKey={this.state.apiKey}
          onSearch={this.onSearch}
          onChangeGenre={this.onChangeGenre}
        />
        <div className={style["items-container"]}>
          {items.map((item, i) => (
            <Item key={item.id} item={item} type={type} itemNum={i} />
          ))}
        </div>
      </div>
    );
  }
}

export default Movies;
