import React, { Component } from "react";
import style from "./style.module.css";
import axios from "axios";
import NavigateBeforeIcon from "@material-ui/icons/NavigateBefore";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";

const PrevButton = props => {
  return (
    <>
      <NavigateBeforeIcon className={style["prev-btn"]} fontSize="large" />
    </>
  );
};

const NextButton = props => {
  return (
    <>
      <NavigateNextIcon className={style["next-btn"]} fontSize="large" />
    </>
  );
};

export class Videos extends Component {
  state = {
    videos: []
  };

  async componentDidMount() {
    const { apiKey, movieID } = this.props;
    await axios
      .get(
        `https://api.themoviedb.org/3/movie/${movieID}/videos?api_key=${apiKey}&language=en-US`
      )
      .then(res => {
        this.setState(() => {
          return {
            videos: res.data.results
          };
        });
      })
      .catch(err => console.log(err));

    //refs
    this.carouselRef = React.createRef();
  }

  transformValue = 0;
  onNext = () => {
    this.transformValue += 100;

    this.carouselRef.current.style.transform = `translateX(-${this.transformValue})`;
  };

  render() {
    return (
      <div className={style.videos}>
        <div ref={this.carouselRef} className={style["video-slide"]}>
          {/* <span>sdfs</span> */}
          {this.state.videos.map(v => (
            <>
              <iframe
                key={v.id}
                src={`https://www.youtube.com/embed/${v.key}`}
                frameborder="0"
              ></iframe>
              <span className={style.spacer}></span>
            </>
          ))}
        </div>
        <PrevButton />
        <NextButton />
      </div>
    );
  }
}

export default Videos;
