import React, { Component } from "react";
import { Link } from "react-router-dom";
import SearchIcon from "@material-ui/icons/Search";
import MenuIcon from "@material-ui/icons/Menu";

const Search = props => {
  return (
    <div className="search-container">
      <input
        type="text"
        onChange={props.onSearch}
        placeholder="Search mophoria.com"
      />
      <div className="results">
        <div className="item"></div>
      </div>
    </div>
  );
};

class Navbar extends Component {
  state = {
    Nav: [
      {
        name: "Discover",
        link: "/discover"
      },
      {
        name: "Movies",
        link: "/movies"
      },
      {
        name: "TV shows",
        link: "/tvshows"
      },
      {
        name: "People",
        link: "/people"
      },
      {
        name: "Genre",
        link: "/genre"
      }
    ],
    SearchedItems: []
  };

  onMenuClick = () => {
    console.log("click");
    let navbar = document.querySelector(".navbar");
    let linkContainer = document.querySelector(".link-container");

    navbar.classList.toggle("navbar-active");
    linkContainer.classList.toggle("link-container-active");
  };

  onSearch = e => {
    console.log(e.target.value);
  };

  render() {
    const apiKey = "325d84ad33eb95a5c0fc5427ba3f569a";
    return (
      <div className="navbar">
        <Search apiKey={this.apiKey} onSearch={this.onSearch} />
        <span className="logo">Mophoria</span>
        <div className="link-container">
          {this.state.Nav.map((nav, i) => (
            <Link key={i} className="link" to={nav.link}>
              {nav.name}
            </Link>
          ))}
        </div>

        <div className="search-button">
          <SearchIcon className="search" fontSize="default" />
        </div>

        <div className="menu-button" onClick={this.onMenuClick}>
          <MenuIcon />
        </div>
      </div>
    );
  }
}

export default Navbar;
