import React from "react";
import axios from "axios";
import PlayArrowIcon from "@material-ui/icons/PlayArrow";
import "./home.css";

const Carousel = props => {
  return (
    <div className="carousel-slide">
      {props.content.map((c, i) => (
        <React.Fragment key={i}>
          <img
            className="poster"
            src={`https://image.tmdb.org/t/p/original${c.poster_path}`}
            alt=""
          />
          {i === 0 && (
            <div className="poster-overlay">
              <span>{c.title}</span>
              <span>{c.vote_average}/10</span>
            </div>
          )}

          <img
            className="backdrop"
            src={`https://image.tmdb.org/t/p/original${c.backdrop_path}`}
            alt="movie backdrop"
          />

          {i === 0 && (
            <div className="backdrop-overlay">
              <PlayArrowIcon
                className="playBtn"
                onClick={props.onPlay}
                fontSize="large"
              />
              <span>{`Watch Trailer of ${c.title}`}</span>
            </div>
          )}
        </React.Fragment>
      ))}
    </div>
  );
};

const ModalVideo = props => {
  return (
    <div className="modal">
      <iframe src="https://www.youtube.com/embed/bwzLiQZDw2I"></iframe>
    </div>
  );
};

class Featured extends React.Component {
  state = {
    selected: {},
    trending: []
  };

  index = 0;
  onPlay = () => {
    console.log("click");
    const modal = document.querySelector(".modal");
    modal.classList.toggle("modal-active");
  };

  onWindowClick = () =>
    window.addEventListener("click", e => {
      console.log(e.target);
      if (e.target.classList.contains("d")) {
        console.log("sdf");
      }
      const modal = document.querySelector(".modal");
      modal.classList.remove("modal-active");
    });

  componentDidMount() {
    axios
      .get(
        `https://api.themoviedb.org/3/trending/movie/day?api_key=${this.props.apiKey}`
      )
      .then(res => {
        this.setState(() => {
          return {
            trending: [...res.data.results]
          };
        });
      })
      .catch(err => {
        console.log(err);
      });

    this.onWindowClick();
  }

  render() {
    return (
      <div className="featured">
        <ModalVideo />
        <Carousel content={this.state.trending} onPlay={this.onPlay} />
      </div>
    );
  }
}

export default Featured;
