import React, { Component } from "react";
import style from "./movies_tvs.module.css";

const Select = props => {
  return (
    <select className={style["select"]}>
      {props.options.map(op => (
        <option>{op}</option>
      ))}
    </select>
  );
};

export class Filter extends Component {
  state = {
    years: ["2020", "2019"],
    width: ""
  };

  manipulateDom = () => {
    console.log(this.inputContainer.current.clientWidth);
  };

  async componentDidMount() {
    this.inputContainer = React.createRef();
    this.selectContainer = React.createRef();
    await this.manipulateDom();
  }

  render() {
    return (
      <div className={style.filter}>
        <div ref={this.inputContainer} className={style["input-container"]}>
          <input
            onChange={e => this.props.onSearch(e)}
            type="text"
            placeholder="Keyword"
          />
        </div>
        <div ref={this.selectContainer} className={style["select-container"]}>
          <Select options={this.state.years} />
          <Select options={this.state.years} />
          <Select options={this.state.years} />
        </div>
      </div>
    );
  }
}

export default Filter;
