import React from "react";
import axios from "axios";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";

const RightBtn = props => {
  return (
    <div className="teathers-rightBtn" onClick={props.onNext}>
      <NavigateNextIcon fontSize="large" />
    </div>
  );
};

class Theaters extends React.Component {
  carouselTransformVal = 0;
  onNext = () => {
    this.carouselTransformVal += 100;
    const carousel = document.querySelector(".theaters-carousel-slide");
    carousel.style.transform = `translateX(-${this.carouselTransformVal}%)`;
  };

  state = {
    inTheaters: [],
    region: "ph"
  };

  async componentDidMount() {
    await axios
      .get(
        `https://api.themoviedb.org/3/movie/now_playing?api_key=${this.props.apiKey}&language=en-US&region=${this.state.region}`
      )
      .then(res => {
        this.setState(() => {
          return {
            inTheaters: [...res.data.results]
          };
        });
      })
      .catch(err => console.log(err));

    console.log(this.state.inTheaters);
  }

  render() {
    return (
      <div className="theaters">
        <React.Fragment>
          <div className="theaters-carousel-slide">
            {this.state.inTheaters.map(m => (
              <div className="theaters-poster">
                <img
                  src={`https://image.tmdb.org/t/p/w500/${m.poster_path}`}
                  alt="movie poster"
                />
                <div className="theaters-overlay">
                  <span>{m.title}</span>
                  <span>{`(${m.release_date})`}</span>
                </div>
              </div>
            ))}
          </div>
          <RightBtn onNext={this.onNext} />
        </React.Fragment>
      </div>
    );
  }
}

export default Theaters;
