import React from "react";
import style from "./global.module.css";
import Title from "./Title";
import CloseIcon from "@material-ui/icons/Close";

const PosterRow = props => {
  return (
    <div className={style["poster-row"]}>
      {props.posters.map(p => (
        <img
          src={`https://image.tmdb.org/t/p/w185/${p.file_path}`}
          alt="poster image"
          onClick={() => {
            props.openNewWindow(p.file_path);
          }}
        />
      ))}
    </div>
  );
};

const BackdropRow = props => {
  return (
    <div className={style["backdrop-row"]}>
      {props.backdrops.map(b => (
        <img
          src={`https://image.tmdb.org/t/p/w300/${b.file_path}`}
          onClick={() => {
            props.openNewWindow(b.file_path);
          }}
        />
      ))}
    </div>
  );
};

const ModalBackground = React.forwardRef((props, ref) => {
  return (
    <div
      ref={ref}
      style={{
        position: "fixed",
        width: "100%",
        height: "110vh",
        top: "0px",
        background: "rgba(0, 0, 0, 0.6)",
        zIndex: 20,
        visibility: "hidden"
      }}
      onClick={props.closeModal}
    ></div>
  );
});

class PictureModal extends React.Component {
  openNewWindow = imgPath => {
    window.open(window.origin + "/image" + imgPath, "_blank");
  };

  closeModal = () => {
    this.modalBackground.current.style.opacity = "0";
    this.modalBackground.current.style.visibility = "hidden";
    this.modalBackground.current.style.transition = "all 200ms ease-in";

    this.pictureModal.current.style.opacity = "0";
    this.pictureModal.current.style.visibility = "hidden";
    this.pictureModal.current.style.transition = "all 200ms ease-in";
  };

  componentDidMount() {
    this.modalBackground = React.createRef();
    this.pictureModal = React.createRef();
  }

  render() {
    const { posters, backdrops } = this.props;

    return (
      <>
        <ModalBackground
          ref={this.modalBackground}
          closeModal={this.closeModal}
          closeModal={this.closeModal}
        />
        <div
          ref={this.pictureModal}
          className={style["pictures-modal"]}
          style={{ zIndex: "21" }}
        >
          <CloseIcon className={style.closeIcon} />
          <Title titleName="Posters" className={style.title} />
          {posters && (
            <PosterRow posters={posters} openNewWindow={this.openNewWindow} />
          )}
          <Title titleName="Backdrops" />
          <BackdropRow
            backdrops={backdrops}
            openNewWindow={this.openNewWindow}
          />
        </div>
      </>
    );
  }
}

export default PictureModal;
