import React from "react";
import axios from "axios";

class Cast extends React.Component {
  state = {
    cast: [],
    crew: []
  };

  componentDidMount() {
    const { apiKey, movieID } = this.props;

    axios
      .get(
        `https://api.themoviedb.org/3/movie/${movieID}/credits?api_key=${apiKey}`
      )
      .then(res =>
        this.setState(() => {
          return {
            cast: [...res.data.cast],
            crew: [...res.data.crew]
          };
        })
      )
      .catch(err => console.log(err));
  }

  render() {
    return <div className="Cast"></div>;
  }
}

export default Cast;
