import React from "react";
import axios from "axios";
import style from "../../movie_tv_components/movie_tv.module.css";
import Overview from "../../movie_tv_components/Overview";
import Title from "../../globalComponents/Title";
import Seasons from "../../movie_tv_components/Seasons";
import Videos from "../../movie_tv_components/Videos";
import Picture from "../../movie_tv_components/Picture";
import Cast from "../../movie_tv_components/Cast";
import List from "../../globalComponents/List";
import Copyright from "../../globalComponents/Copyright";
import PictureModal from "../../globalComponents/PictureModal";
import CastModal from "../../globalComponents/CastModal";

const SeasonModal = props => {
  return (
    <>
      <div className={style["season-modal-background"]}></div>
      <div className={style["season-modal"]}>
        <Title titleName={`Season`} />
      </div>
    </>
  );
};

class Tv extends React.Component {
  tvID = () => {
    const { tvID } = this.props.match.params;
    return tvID;
  };

  state = {
    apiKey: "325d84ad33eb95a5c0fc5427ba3f569a",
    tvID: this.tvID(),
    details: {},
    posters: [],
    backdrops: [],
    cast: [],
    crew: []
  };

  setImages = (posters, backdrops) => {
    this.setState(() => {
      return {
        posters: [...posters],
        backdrops: [...backdrops]
      };
    });
  };

  setCast = (cast, crew) => {
    this.setState(() => {
      return {
        cast: [...cast],
        crew: [...crew]
      };
    });
  };

  setModalVisible = (background, modal) => {
    background.current.style.visibility = "visible";
    background.current.style.opacity = "1";
    background.current.style.transition = "all 200ms ease-in";

    modal.current.style.visibility = "visible";
    modal.current.style.opacity = "1";
    modal.current.style.transition = "all 200ms ease-in";
  };

  openPictureModal = () => {
    this.pictureModalBackground.current.style.visibility = "visible";
    this.pictureModalBackground.current.style.opacity = "1";
    this.pictureModalBackground.current.style.transition = "all 200ms ease-in";

    this.pictureModal.current.style.visibility = "visible";
    this.pictureModal.current.style.opacity = "1";
    this.pictureModal.current.style.transition = "all 200ms ease-in";
  };

  openCastModal = () => {
    this.castModalBackground.current.style.visibility = "visible";
    this.castModalBackground.current.style.opacity = "1";
    this.castModalBackground.current.style.transition = "all 200ms ease-in";

    this.castModal.current.style.visibility = "visible";
    this.castModal.current.style.opacity = "1";
    this.castModal.current.style.transition = "all 200ms ease-in";
  };

  openSeasonModal = () => {
    this.setModalVisible(this.seasonBackgroundModal, this.seasonModal);
  };

  async componentDidMount() {
    const { apiKey, tvID } = this.state;
    this.pictureModalBackground = React.createRef();
    this.pictureModal = React.createRef();
    this.castModalBackground = React.createRef();
    this.castModal = React.createRef();
    this.seasonBackgroundModal = React.createRef();
    this.seasonModal = React.createRef();

    await axios
      .get(
        `https://api.themoviedb.org/3/tv/${tvID}?api_key=${apiKey}&language=en-US`
      )
      .then(res => {
        this.setState(() => {
          return {
            details: res.data
          };
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div style={{ display: "grid", gridTemplateColumns: "1.2fr 8fr 1.2fr" }}>
        <PictureModal
          ref={{
            firstRef: this.pictureModalBackground,
            secondRef: this.pictureModal
          }}
          posters={this.state.posters}
          backdrops={this.state.backdrops}
        />
        <CastModal
          ref={{ background: this.castModalBackground, modal: this.castModal }}
          cast={this.state.cast}
          crew={this.state.crew}
        />

        <SeasonModal />

        <Overview details={this.state.details} />
        <Title titleName="Seasons" />
        <Seasons
          seriesName={this.state.details.name}
          seasons={this.state.details.seasons}
          open={this.openSeasonModal}
        />
        <Title titleName="Videos" />
        <Videos
          isMovie={false}
          apiKey={this.state.apiKey}
          itemID={this.state.tvID}
        />
        <Title
          titleName="Pictures"
          more={true}
          onClickViewAll={this.openPictureModal}
        />
        <Picture
          isMovie={false}
          itemID={this.state.tvID}
          apiKey={this.state.apiKey}
          setImages={this.setImages}
        />
        <Title
          titleName="Cast"
          more={true}
          onClickViewAll={this.openCastModal}
        />
        <Cast
          isMovie={false}
          apiKey={this.state.apiKey}
          itemID={this.state.tvID}
          setCast={this.setCast}
        />
        <Title titleName="Similar tv shows" />
        <List
          api={`https://api.themoviedb.org/3/tv/${this.state.tvID}/similar?api_key=${this.state.apiKey}&language=en-US&page=1`}
          isMovie={false}
        />
        <Copyright />
      </div>
    );
  }
}

export default Tv;
