import React, { Component } from "react";
import axios from "axios";
import style from "./style.module.css";
import Overview from "./Overview";

const Title = props => {
  const labelStyle = {
    color: "white",
    fontWeight: "bold",
    fontSize: "1.8em",
    marginLeft: "4px"
  };

  return (
    <div
      style={{ display: "flex", alignItems: "center", gridColumnStart: "2" }}
    >
      <div
        style={{
          width: "4px",
          height: "28px",
          backgroundColor: "#c7493a"
        }}
      ></div>
      <label className="component-label" style={labelStyle}>
        {props.titleName}
      </label>
    </div>
  );
};

export class Movie extends Component {
  state = {
    details: {}
  };

  async componentDidMount() {
    const { movieID } = this.props.match.params;
    const apiKey = "325d84ad33eb95a5c0fc5427ba3f569a";

    await axios
      .get(
        `https://api.themoviedb.org/3/movie/${movieID}?api_key=${apiKey}&language=en-US`
      )
      .then(res => {
        this.setState(() => {
          return {
            details: res.data
          };
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div className={style.movie}>
        <Overview details={this.state.details} />
        <Title titleName="Videos" />
      </div>
    );
  }
}

export default Movie;
